export default function CardSliding() {
  const [selectedCard, setSelectedCard] = useState(0);
  const [change, setChange] = useState(true);

  useEffect(() => {
    const timeout = setTimeout(() => setChange(true), 300);

    return () => {
      clearTimeout(timeout);
    };
  }, [selectedCard]);

  return (
    <Wrapper className="isolate px-8">
      <div
        className="relative isolate mx-auto flex min-h-96 w-full max-w-5xl items-center overflow-hidden rounded-xl bg-white py-12"
        style={{
          // @ts-ignore
          "--userLogoColor": "white",
          boxShadow:
            "0 50px 100px -20px rgba(50,50,93,0.25), 0 30px 60px -30px rgba(0,0,0,0.3)",
        }}
      >
        {cards.map((card) => (
          <div
            key={card.id}
            className="grid w-full items-center gap-8 p-8 duration-700 ease-in-out md:grid-cols-2 md:gap-4"
            style={{
              flex: "0 0 auto",
              transform: `translateX(-${selectedCard * 100}%)`,
              opacity: selectedCard === card.id - 1 ? 1 : 0,
            }}
          >
            <div>
              <p
                className={`text-xl text-white before:absolute before:-translate-x-3 before:content-['_"'] after:content-['_"']`}
              >
                {card.text}
              </p>
            </div>
            <div className="flex items-center justify-center text-white">
              <div className="w-96">{card.logo}</div>
            </div>
          </div>
        ))}

        {/* Background */}
        <div
          className="absolute inset-0 -z-10 duration-1000"
          style={{ background: cards[selectedCard].color }}
        />
      </div>

      <div className="lg::gap-y-0 mt-12 grid grid-cols-2 gap-y-8 lg:grid-cols-4">
        {cards.map((card, index) => (
          <button
            key={card.id}
            className="w-full max-w-64 px-4"
            onClick={() => {
              setSelectedCard(index);
              setChange(false);
            }}
            style={
              index === selectedCard && change
                ? {}
                : // @ts-ignore
                  { "--userLogoColor": "#aab4c1" }
            }
          >
            {card.logo}
          </button>
        ))}
      </div>

      {/* Grid Lines */}
      <div className="absolute isolate -z-20 grid h-full w-full max-w-5xl grid-cols-2 overflow-hidden lg:grid-cols-4">
        <div className="h-full w-px bg-[#aab4c1]/20"></div>
        <svg className="hidden h-full lg:block">
          <line
            x1="0"
            y1="0"
            x2="0"
            y2="100%"
            strokeWidth={3}
            strokeDasharray={8}
            className="stroke-[#aab4c1]/20"
          />
        </svg>
        <svg className="hidden h-full lg:block">
          <line
            x1="0"
            y1="0"
            x2="0"
            y2="100%"
            strokeWidth={3}
            strokeDasharray={8}
            className="stroke-[#aab4c1]/20"
          />
        </svg>
        <svg className="h-full">
          <line
            x1="0"
            y1="0"
            x2="0"
            y2="100%"
            strokeWidth={3}
            strokeDasharray={8}
            className="stroke-[#aab4c1]/20"
          />
        </svg>
        <div className="absolute right-0 top-0 h-full w-px bg-[#aab4c1]/20"></div>
      </div>
    </Wrapper>
  );
}
